window.addEventListener("resize",adjustListBorder);let listRemoveWidth=window.matchMedia("(max-width: 1070px)"),listAddWidth=window.matchMedia("(min-width: 1070px)");function adjustListBorder(){listRemoveWidth.matches?document.getElementById("articlesList").classList.remove("author-alc"):listAddWidth.matches&&document.getElementById("articlesList").classList.add("author-alc")}